import base64
import PIL
from PIL import Image, ImageDraw

r = {"capcode": "1648458559239",
     "slidingImage": "",
     "backImage": "",
     "yHeight": "39"}

slidingImage = r['slidingImage']
backImage = r['backImage']

image_data = base64.b64decode(slidingImage)
backImage = base64.b64decode(backImage)

with open('slidingImage.jpg', 'wb') as f:
    f.write(image_data)
with open('backImage.jpg', 'wb') as f:
    f.write(backImage)


def get_slider_offset_method3(pic_path, cut_path, threshold=60):
    """比较两张图片的像素点RGB的绝对值是否小于阈值60,如果在阈值内则相同,反之不同"""
    pic_img = Image.open(pic_path)
    cut_img = Image.open(cut_path)
    width, height = pic_img.size
    for x in range(40, width - 40):  # 从左往右
        for y in range(5, height - 10):  # 从上往下
            pixel1 = pic_img.load()[x, y]
            pixel2 = cut_img.load()[x, y]
            if abs(pixel1[0] - pixel2[0]) < threshold and abs(pixel1[1] - pixel2[1]) < threshold and abs(
                    pixel1[2] - pixel2[2]) < threshold:
                continue
            else:
                ImageDraw.ImageDraw(cut_img).rectangle([(x - 10, y - 10), (x + 52, y + 50)], outline='red')
                cut_img.show()
                return x - 6


get_slider_offset_method3('backImage.jpg', 'slidingImage.jpg')
